insert into biz_cases values(biz_cases_seq.nextval,'노원구의 평온한 분위기를 반영한 실용적 인테리어','지금부터 소개할 것은 현대적인 감각이 돋보이는 욕실 리모델링입니다. 욕실은 개인의 취향을 반영하는 동시에, 청결하고 기능적인 공간이어야 합니다. 저희는 클라이언트의 스타일을 반영해 최신 트렌드를 접목한 세련된 욕실을 제작했어요. 혁신적인 수납 솔루션과 세련된 타일 조합으로, 일상의 목욕 시간을 특별한 휴식의 시간으로 변모시켰습니다.','디자인스퀘어', systimestamp, 0,'61112');
insert into biz_cases values(biz_cases_seq.nextval,'관악산을 닮은 푸른 녹지와 어우러진 인테리어',' 이번에는 현대적 감각이 돋보이는 용산 파크뷰의 거실을 재해석한 인테리어 프로젝트를 선보일 예정입니다. 거실은 가정의 얼굴이자 휴식과 소통의 장으로서 그 가치가 더욱 중요해지고 있는데요, 우리는 클라이언트가 꿈꾸는 이상적인 거실 공간을 실현하기 위해 맞춤형 디자인을 제공합니다. 쾌적한 분위기 조성은 물론, 현대적인 가구 배치와 공간 활용의 팁을 통해, 가족 모두가 만족할 수 있는 거실 공간을 만들어 드리겠습니다!','디자인스퀘어', systimestamp, 0,'61112');
insert into biz_cases values(biz_cases_seq.nextval,'일산의 현대적인 도시 라이프를 위한 인테리어','고객님의 일상에 꼭 맞는 개성 있는 주거 공간을 제공합니다. 이번에는 트렌디한 감각이 돋보이는 명동 스카이 레지던스의 침실 인테리어를 소개해드리려 합니다. 침실은 개인의 휴식과 재충전을 위한 공간으로, 디자인과 기능성이 조화를 이루어야 하는 중요한 장소입니다. 맞춤형 수납장 설계로 깔끔함을 유지하면서도, 세련된 침실 디자인으로 여러분의 퀄리티 있는 삶을 지원하겠습니다. 컬러 매치부터 소품 선정까지, 디테일에 이르기까지 우리는 여러분의 침실이 단순한 잠자리가 아닌, 삶의 질을 높이는 공간이 되도록 만들어 갑니다!','디자인스퀘어', systimestamp, 0,'61112');
insert into biz_cases values(biz_cases_seq.nextval,'종로의 전통과 현대적 감각이 어우러진 고급스러운 인테리어','고객님의 이야기가 담긴 생활 공간을 만드는 데 집중합니다. 오늘은 근사한 강남 엘리트 하우스의 아이 방 리모델링 프로젝트를 공개합니다. 아이 방은 아이의 성장은 물론, 학습과 놀이가 함께하는 다용도 공간으로 기능해야 합니다. 우리는 고객님의 아이가 즐겁고 건강하게 자랄 수 있는 환경을 만들기 위해 안전한 재료 선택과 창의적인 공간 배치를 추구합니다. 학습 효율을 높여주는 책상 배치부터, 재미있는 놀이 공간까지, 아이의 성장 과정에 맞춘 인테리어로 가득 채울 것입니다!','디케이메이크', systimestamp, 0,'23439');
insert into biz_cases values(biz_cases_seq.nextval,'고양시 덕양구의 자연과 어우러진 실내공간','오늘은 특별히 세련된 도시적 감성을 담은 한남동 레지던스의 거실 인테리어를 소개해 드릴 예정입니다. 이 공간은 도심 속 현대적 생활을 반영해 모더니티와 편안함을 조화롭게 담아내고자 했습니다. 집안 곳곳에 세심하게 배치된 아트워크와 함께, 휴식과 대화가 어우러지는 거실 공간을 만들어 더욱 의미 있는 시간을 보낼 수 있도록 설계했습니다.','디케이메이크', systimestamp, 0,'23439');
insert into biz_cases values(biz_cases_seq.nextval,'강변의 멋진 전망을 갖춘 영등포구 아파트 인테리어','이번에는 자연 채광을 극대화한 서초동 펜트하우스의 침실 인테리어를 보여드릴 것입니다. 이 공간은 대형 창문을 통해 들어오는 햇빛을 이용해 여유로운 아침을 맞이하실 수 있도록 디자인되었습니다. 우드 소재와 화이트 컬러를 기반으로 한 인테리어가 포근하고 안정된 수면을 도와드릴 거예요.','디케이메이크', systimestamp, 0,'23439');
insert into biz_cases values(biz_cases_seq.nextval,'안양시 동안구의 모던한 라이프 스타일을 반영한 인테리어','오늘은 현대와 전통이 공존하는 분위기를 연출한 북촌 한옥의 다락방 인테리어를 선보이고자 합니다. 전통적인 요소를 현대적 감각으로 재해석하여, 전통의 멋과 현대적 편의성이 어우러진 고유의 공간을 만들어냈습니다. 목재와 한지를 사용한 인테리어로 고즈넉한 분위기 속에서 여유를 만끽하실 수 있습니다.','디케이메이크', systimestamp, 0,'23439');
insert into biz_cases values(biz_cases_seq.nextval,'양천구의 푸르른 자연과 어우러진 인테리어 디자인','오늘은 활기찬 색채가 돋보이는 강남 스타일리스트 살롱의 내부 인테리어를 소개해 드립니다. 이곳은 고객님의 에너지와 창의력이 샘솟도록 활기찬 색상과 패턴으로 가득 차 있습니다.','디케이메이크', systimestamp, 0,'23439');
insert into biz_cases values(biz_cases_seq.nextval,'자연친화적이고 실용적인 집 인테리어','도심 한복판에서 자연과 어우러지는 휴식을 취할 수 있는 거실 인테리어를 소개해 드리고자 합니다. 우리는 집안에 식물을 풍성하게 배치하고, 자연 소재를 사용하여 공간에 생명을 불어넣었습니다. 햇빛이 잘 들어오는 창가에는 작은 허브 정원을 마련하여 신선함을 느낄 수 있도록 했죠. 자연스러운 컬러 팔레트와 목재의 따뜻함이 조화를 이루는 이 거실은 마치 숲속의 캐빈에 있는 듯한 평화로움을 선사합니다.','와이디자인', systimestamp, 0,'22243');
insert into biz_cases values(biz_cases_seq.nextval,'현대적이고 아늑한 광명시 아파트 인테리어','최근 재택근무가 일상이 된 만큼, 집에서도 업무에 집중할 수 있는 공간이 필수가 되었죠. 오늘은 고객님의 업무 효율성을 극대화하기 위해 디자인한 미니멀리즘 홈 오피스를 공유하려 합니다. 흰색과 검정색의 대조적인 컬러 스킴을 사용하여 집중을 방해하는 요소를 최소화했습니다. 또한, 맞춤형 책상과 수납장을 통해 최대한의 수납 공간을 확보하면서도 깔끔한 라인을 유지했습니다. 단순하지만 강력한 이 공간은 효율적인 작업환경을 만드는 데 중점을 두었습니다.','와이디자인', systimestamp, 0,'22243');
insert into biz_cases values(biz_cases_seq.nextval,'구로구의 현대적인 도시 라이프를 반영한 인테리어','오늘은 특별히 연인들을 위한 로맨틱한 침실 인테리어를 소개해 드립니다. 부드러운 조명, 따스한 패브릭, 그리고 섬세한 장식으로 이루어진 이 침실은 두 사람만의 달콤한 공간이 되도록 설계되었습니다. 벽면 하나를 선택하여 사진과 추억이 담긴 갤러리 벽을 만들고, 부드러운 커튼과 침구로 아늑함을 더했습니다. 이곳은 일상에서 벗어나 서로를 위한 시간을 만끽할 수 있는 완벽한 공간입니다.','와이디자인', systimestamp, 0,'22243');
insert into biz_cases values(biz_cases_seq.nextval,'강서구의 자연과 어우러진 편안한 인테리어 공간','오늘은 기능성과 스타일이 조화를 이룬 현대적인 주방 리모델링 사례를 보여드립니다. 고객님의 요리 활동을 고려하여 실용적인 구조의 주방을 설계하였고, 최신 가전제품을 통합하여 현대적인 편의성을 더했습니다. 세련된 메탈릭 터치와 모던한 캐비닛 디자인으로 깔끔하면서도 풍부한 저장 공간을 제공합니다. 이 주방은 모임의 중심지가 될 것이며 가족 간의 소통을 더욱 풍부하게 해줄 것입니다.','와이디자인', systimestamp, 0,'22243');
insert into biz_cases values(biz_cases_seq.nextval,'노원구의 평화로운 분위기를 담은 아늑한 인테리어','책을 사랑하는 고객님을 위해 아늑한 북 카페 스타일의 서재를 조성했습니다. 따뜻한 목재 책장과 소프트한 조명은 독서에 최적화된 환경을 제공하며, 편안한 암체어는 긴 시간 독서에도 편안함을 줍니다. 여러분의 좋아하는 커피 한 잔과 함께 책 속 세계로 빠져볼 수 있는 이 공간은 일상에서 벗어나 마음의 여유를 즐길 수 있는 특별한 곳입니다.','와이디자인', systimestamp, 0,'22243');
insert into biz_cases values(biz_cases_seq.nextval,'관악구의 자연 친화적이고 세련된 인테리어','여러분의 삶에 휴식과 상쾌함을 더하는 욕실 디자인을 제안합니다. 우리는 편리함과 휴식이 공존하는 욕실 공간을 만들기 위해 현대적인 설비와 세련된 소재를 선택했습니다. 넉넉한 수납공간과 함께, 깔끔한 라인의 샤워 부스와 목욕탕을 설계하여 사용자의 편의를 최우선으로 생각했습니다. 밝은 컬러와 현대적인 질감이 조화를 이루어, 매일의 시작과 끝을 기분 좋게 할 수 있는 공간을 선사합니다.','아이바라', systimestamp, 0,'45624');
insert into biz_cases values(biz_cases_seq.nextval,'일산의 고급스러운 도시 라이프를 반영한 인테리어','오늘은 즐겁고 활기찬 파스텔 톤의 키즈룸을 선보입니다. 안전하고 친환경적인 소재로 아이들의 상상력을 자극하는 다채로운 디자인을 구현했습니다. 벽면에는 교육적이면서 재미있는 벽화를 그려 넣어 아이들이 즐겁게 배울 수 있는 공간을 만들었고, 키즈 가구는 사용성과 안전을 고려하여 선택했습니다. 이 공간은 아이들에게 창의력을 키우는 놀이터이자 학습 공간으로 변신합니다.','아이바라', systimestamp, 0,'45624');
insert into biz_cases values(biz_cases_seq.nextval,'종로의 전통과 현대적 감각이 조화로운 고급 인테리어','다이닝 룸은 단순히 식사를 하는 장소 이상의 의미를 가져야 합니다. 저희는 이번 프로젝트에서 현대적이고 심플한 디자인을 통해 다이닝 룸을 완전히 새롭게 탈바꿈시켰습니다. 세련된 테이블과 편안한 좌석이 조화를 이루며, 조명은 식사 분위기를 더욱 돋보이게 합니다. 이곳에서 가족들과 함께하는 식사 시간은 더욱 기억에 남는 순간이 될 것입니다.','아이바라', systimestamp, 0,'45624');
insert into biz_cases values(biz_cases_seq.nextval,'현대적인 주거 공간: 작지만 효율적인 집 구조 디자인','오늘은 빈티지한 매력과 현대적 감각이 어우러진 거실 리노베이션 사례를 소개합니다. 과거의 아름다움을 간직한 빈티지 가구와 모던한 소품들이 조화를 이루어 고유의 스타일을 창출합니다. 원목 바닥과 앤티크 장식품은 클래식한 멋을, 현대적인 소파와 테이블은 사용자의 편리성을 더해주죠. 이러한 조화로운 디자인은 거실을 더욱 독특하고 개성 있는 공간으로 만들어줍니다.','아이바라', systimestamp, 0,'45624');
insert into biz_cases values(biz_cases_seq.nextval,'생활 편의성을 높이는 작은집 주방 확장 프로젝트','은퇴 후 여유로운 삶을 꿈꾸시는 분들을 위한 안락한 리빙룸 디자인을 선사합니다. 우리는 취미와 휴식을 모두 즐길 수 있는 공간을 마련하여 고객님의 라이프스타일에 맞춰 진정한 휴식처를 제공합니다. 부드러운 색상의 소파와 리클라이너, 여유로운 독서 공간과 함께 음악을 감상할 수 있는 공간을 설계했습니다. 이 공간은 여러분의 새로운 취미를 발견하고, 일상의 여유를 만끽하는 곳이 될 것입니다.','아이바라', systimestamp, 0,'45624');
insert into biz_cases values(biz_cases_seq.nextval,'쾌적한 환경을 위한 라인 조명과 시스템 에어컨','마음의 평화를 찾을 수 있는 특별한 공간을 조성했습니다. 바쁜 일상에서 벗어나 명상과 요가를 즐길 수 있는 공간을 만들어, 고객님의 정신적, 신체적 웰빙을 지원합니다. 자연광이 가득한 방에는 부드러운 쿠션과 마음을 진정시키는 색상들로 꾸며져 있으며, 모든 요소들이 조화를 이루어 최상의 평온을 선사합니다.','에이치디자인', systimestamp, 0,'56454');
insert into biz_cases values(biz_cases_seq.nextval,'낭만적인 분위기와 휴식 공간이 조화를 이루는 주택','이번에는 스칸디나비아 디자인에서 영감을 받아, 깨끗하고 심플한 선들이 돋보이는 주방을 완성했습니다. 밝은 목재 바닥과 화이트 캐비닛, 그리고 세련된 스테인리스 스틸 조리기구가 모던함을 더해주며, 여유로운 아침 식사를 즐길 수 있는 작은 아일랜드도 설계했습니다.','에이치디자인', systimestamp, 0,'56454');
